PRAGMA foreign_keys=OFF;
BEGIN TRANSACTION;
CREATE TABLE Books(
		Id INTEGER PRIMARY KEY AUTOINCREMENT,
		BookTitle TEXT,
		Author TEXT,
		Editor TEXT,
		Category TEXT,
		SubCategory TEXT,
		Publisher TEXT,
		PublishedDate TEXT,
		Edition TEXT,
		Language TEXT,
		ShelfLocation TEXT,
		ISBN TEXT,
		Notes TEXT,
		Read INTEGER,
		DateAdded TEXT,
		DateAcquired TEXT,
		ImageLink TEXT
	, PageCount INTEGER, LastUpdated TEXT);
CREATE UNIQUE INDEX idx_books_isbn ON Books(ISBN);
COMMIT;
